import React, { useEffect, useState, useRef } from "react";
import { Printer, Download, ArrowLeft, CheckCircle } from "lucide-react";
import { useLocation } from "react-router-dom";

const Invoice = ({ onBack }) => {
  const location = useLocation();
  const [job, setJob] = useState(location.state || null);
  const [loading, setLoading] = useState(!job);
  const invoiceRef = useRef(null);

  useEffect(() => {
    // Set document title to invoice number
    if (job && job.invoiceNumber) {
      document.title = job.invoiceNumber;
    }

    if (!job) {
      setTimeout(() => {
        setJob({
          invoiceNumber: "INV-2025-015",
          name: "",
          address: "",
          instructions: "",
          date: new Date().toISOString().split("T")[0],
          servicePrice: 0,
          transport: 0,
          tax: 0,
          totalPrice: 0,
          items: [
            { description: "Basic Wash & Wax", price: 55 },
            { description: "Interior Deep Clean", price: 80 },
            { description: "Paint Protection", price: 120 }
          ],
        });
        setLoading(false);
      }, 1000);
    }
  }, [job]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  const handlePrint = () => {
    window.print();
  };

  const handleDownload = () => {
    // Use the invoice number as the filename
    const filename = job.invoiceNumber + ".pdf";

    // This is a simplified approach - for actual PDF generation
    // you would need to use a library like jsPDF or react-pdf

    // For now, we'll use print functionality but with proper title
    document.title = job.invoiceNumber;
    window.print();

    // Reset title after printing
    setTimeout(() => {
      document.title = "Invoice System";
    }, 100);
  };

  // Calculate totals
  const subtotal = job.items.reduce((sum, item) => sum + item.price, 0);

  return (
    <div className="bg-gray-50 min-h-screen p-4 md:p-6 print:p-0 print:bg-white">
      {/* Top Bar */}
      <div className="max-w-3xl mx-auto mb-4 print:hidden">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
          <button onClick={onBack} className="flex items-center text-gray-600 hover:text-gray-900 transition-colors">
            <ArrowLeft className="h-5 w-5 mr-1" />
            Back to Dashboard
          </button>
          <div className="flex space-x-3">
            <button
              onClick={handlePrint}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center hover:bg-blue-700 transition-colors"
            >
              <Printer className="h-5 w-5 mr-2" />
              Print
            </button>
            <button 
              onClick={handleDownload}
              className="bg-gray-800 text-white px-4 py-2 rounded-lg flex items-center hover:bg-gray-900 transition-colors"
            >
              <Download className="h-5 w-5 mr-2" />
              Download PDF
            </button>
          </div>
        </div>
      </div>

      {/* Invoice Document */}
      <div 
        ref={invoiceRef}
        className="max-w-3xl mx-auto bg-white shadow-lg rounded-xl overflow-hidden print:shadow-none print:rounded-none border print:border-0 border-gray-100"
      >
        {/* Color Bar */}
        <div className="h-2 bg-gradient-to-r from-blue-500 to-blue-700"></div>

        <div className="p-6 print:p-4">
          {/* Header */}
          <div className="border-b border-gray-100 pb-4 mb-4">
