import React, { useState, useEffect } from "react";
import { Printer, Download, ArrowLeft } from "lucide-react";
import { useLocation } from "react-router-dom";

const Invoice = ({ jobId, onBack }) => {
  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  // Simulate fetching job data
  useEffect(() => {
    setTimeout(() => {
    //   setJob(
    //     location.state?. || {
    //       invoiceNumber: "INV-2025-015",
    //       date: "11 Apr 2025",
    //       name: "Sarah Johnson",
    //       address: "42 Oakwood Drive, Southampton, SO19 8EF",
    //       phone: "07712 345678",
    //       email: "sarah.johnson@example.com",
    //       instructions: "Please detail the exterior and interior. Extra attention to leather seats.",
    //       items: [
    //         { description: "Full Vehicle Detailing", unitPrice: 120, qty: 1, total: 120 },
    //         { description: "Premium Wax Treatment", unitPrice: 45, qty: 1, total: 45 },
    //         { description: "Leather Conditioning", unitPrice: 35, qty: 1, total: 35 },
    //       ],
    //       subtotal: 200,
    //       tax: 40,
    //       transport: 25,
    //       totalDue: 265,
    //       dueDate: "25 Apr 2025",
    //     }
    //   );
      setLoading(false);
    }, 1000);
  }, [location.state]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  const handlePrint = () => {
    window.print();
  };

  return (
    <div className="bg-gray-50 min-h-screen p-4 md:p-8">
      {/* Top Bar with actions */}
      <div className="max-w-4xl mx-auto mb-8 print:hidden">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
          <button
            onClick={onBack}
            className="flex items-center text-gray-600 hover:text-gray-900"
          >
            <ArrowLeft className="h-5 w-5 mr-1" />
            Back to Dashboard
          </button>

          <div className="flex space-x-3">
            <button
              onClick={handlePrint}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center hover:bg-blue-700 transition-colors"
            >
              <Printer className="h-5 w-5 mr-2" />
              Print
            </button>

            <button className="bg-gray-800 text-white px-4 py-2 rounded-lg flex items-center hover:bg-gray-900 transition-colors">
              <Download className="h-5 w-5 mr-2" />
              Download PDF
            </button>
          </div>
        </div>
      </div>

      {/* Invoice Paper */}
      <div className="max-w-4xl mx-auto bg-white shadow-md rounded-lg overflow-hidden print:shadow-none print:rounded-none">
        <div className="p-8 print:p-6">
          {/* Header */}
          <div className="border-b pb-6 mb-6">
            <div className="flex flex-col md:flex-row justify-between items-start md:items-center">
              <div className="mb-4 md:mb-0">
                <h1 className="text-2xl font-bold text-gray-800 tracking-tight">DANIEL MOBILE DETAILING</h1>
                <div className="text-gray-600 mt-1">
                  <p>75 Heath Road, Locks Heath,</p>
                  <p>Southampton, SO31 6BJ</p>
                  <p className="mt-1">Phone: 078 69 99 7211</p>
                </div>
              </div>

              <div className="bg-gray-100 p-4 rounded-lg">
                <div className="flex justify-between mb-1">
                  <span className="font-bold text-gray-600">INVOICE NO:</span>
                  <span className="font-bold text-blue-600">{location.state.invoiceNumber}</span>
                </div>
                <div className="flex justify-between">
                  <span className="font-bold text-gray-600">DATE:</span>
                  <span>{location.state.date}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Client & Instructions */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
            <div>
              <h2 className="text-lg font-bold text-gray-700 mb-3">BILL TO</h2>
              <div className="text-gray-600">
                <p className="font-medium text-gray-800">{location.state.name}</p>
                <p>{location.state.address}</p>
                <p className="mt-2">Phone: {location.state.phone}</p>
                <p>Email: {location.state.email}</p>
              </div>
            </div>

            <div>
              <h2 className="text-lg font-bold text-gray-700 mb-3">INSTRUCTIONS</h2>
              <p className="text-gray-600">{location.state.instructions}</p>
            </div>
          </div>

          {/* Invoice Items */}
          <div className="mb-8">
            <table className="w-full">
              <thead>
                <tr className="border-b-2 border-gray-200">
                  <th className="text-left py-3 px-2 text-gray-600">DESCRIPTION</th>
                  <th className="text-right py-3 px-2 text-gray-600">UNIT PRICE</th>
                  <th className="text-right py-3 px-2 text-gray-600">QTY</th>
                  <th className="text-right py-3 px-2 text-gray-600">TOTAL</th>
                </tr>
              </thead>
              <tbody>
                {location.state.items.map((item, index) => (
                  <tr key={index} className="border-b border-gray-100">
                    <td className="py-4 px-2 text-gray-800">{item.description}</td>
                    <td className="py-4 px-2 text-right text-gray-800">£{item.unitPrice.toFixed(2)}</td>
                    <td className="py-4 px-2 text-right text-gray-800">{item.qty}</td>
                    <td className="py-4 px-2 text-right font-medium text-gray-800">£{item.total.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Totals */}
          <div className="flex justify-end">
            <div className="w-full md:w-64">
              <div className="flex justify-between py-2 text-gray-600">
                <span>SUBTOTAL</span>
                <span className="font-medium">£{location.state.subtotal.toFixed(2)}</span>
              </div>
              <div className="flex justify-between py-2 text-gray-600">
                <span>SALES TAX</span>
                <span className="font-medium">£{location.state.tax.toFixed(2)}</span>
              </div>
              <div className="flex justify-between py-2 text-gray-600">
                <span>TRANSPORT & HANDLING</span>
                <span className="font-medium">£{location.state.transport.toFixed(2)}</span>
              </div>
              <div className="flex justify-between py-3 text-lg font-bold border-t-2 border-gray-200 mt-2">
                <span>TOTAL DUE BY {location.state.dueDate}</span>
                <span className="text-blue-600">£{location.state.totalDue.toFixed(2)}</span>
              </div>
            </div>
          </div>

          {/* Thank You Note */}
          <div className="text-center mt-12 text-gray-600 italic">
            <p>Thank you for your business!</p>
          </div>
        </div>
      </div>
    </div>
  );
};

