import React, { useEffect, useState } from "react";
import { Printer, Download, ArrowLeft, CheckCircle } from "lucide-react";
import { useLocation } from "react-router-dom";

const Invoice = ({ onBack }) => {
  const location = useLocation();
  const [job, setJob] = useState(location.state || null);
  const [loading, setLoading] = useState(!job);

  useEffect(() => {
    if (!job) {
      setTimeout(() => {
        setJob({
          invoiceNumber: "INV-2025-015",
          name: "",
          address: "",
          instructions: "",
          date: new Date().toISOString().split("T")[0],
          servicePrice: 0,
          transport: 0,
          tax: 0,
          totalPrice: 0,
          services: [
            { description: "Basic Wash & Wax", price: 55 },
            { description: "Interior Deep Clean", price: 80 },
            { description: "Paint Protection", price: 120 }
          ],
        });
        setLoading(false);
      }, 1000);
    }
    console.log(job)
  }, [job]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  const handlePrint = () => {
    window.print();
  };

  const handleDownload = () => {
    // Placeholder for PDF download functionality
    window.print();
  };

  // Calculate totals
  const subtotal = 100;

  return (
    <div className="bg-gray-50 min-h-screen p-4 md:p-8">
      {/* Top Bar */}
      <div className="max-w-4xl mx-auto mb-8 print:hidden">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
          <button onClick={onBack} className="flex items-center text-gray-600 hover:text-gray-900 transition-colors">
            <ArrowLeft className="h-5 w-5 mr-1" />
            Back to Dashboard
          </button>
          <div className="flex space-x-3">
            <button
              onClick={handlePrint}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center hover:bg-blue-700 transition-colors"
            >
              <Printer className="h-5 w-5 mr-2" />
              Print
            </button>
            <button 
              onClick={handleDownload}
              className="bg-gray-800 text-white px-4 py-2 rounded-lg flex items-center hover:bg-gray-900 transition-colors"
            >
              <Download className="h-5 w-5 mr-2" />
              Download PDF
            </button>
          </div>
        </div>
      </div>

      {/* Invoice Details */}
      <div className="max-w-4xl mx-auto bg-white shadow-lg rounded-xl overflow-hidden print:shadow-none print:rounded-none border print:border-0 border-gray-100">
        {/* Color Bar */}
        <div className="h-2 bg-gradient-to-r from-blue-500 to-blue-700"></div>

        <div className="p-8 print:p-6">
          {/* Header */}
          <div className="border-b border-gray-100 pb-6 mb-6">
            <div className="flex flex-col md:flex-row justify-between items-start md:items-center">
              <div>
                <h1 className="text-3xl font-bold text-gray-800 tracking-tight">DANIEL MOBILE DETAILING</h1>
                <div className="text-gray-500 mt-2 flex-left">
                  <p>75 Heath Road, Locks Heath,</p>
                  <p>Southampton, SO31 6BJ</p>
                  <p>078 69 99 7211 </p>
                </div>
              </div>
              <div className="bg-gray-50 p-5 rounded-lg shadow-sm border border-gray-100 md:text-right mt-4 md:mt-0">
                <h2 className="font-bold text-2xl text-blue-600 mb-2">INVOICE</h2>
                <div className="flex justify-between mb-1">
                  <span className="font-medium text-gray-500">INVOICE NO:</span>
                  <span className="font-bold text-gray-800">{job.invoiceNumber}</span>
                </div>
                <div className="flex justify-between">
                  <span className="font-medium text-gray-500">DATE:</span>
                  <span className="text-gray-800">{job.date}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Client Details */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
